    className='menu-item'
    >
        <div className='content'>
            <h1 className='title'>{title}</h1> {/*Equal to saying props.title*/}
            <span className='subtitle'>SHOP NOW</span>
        </div>
        <p>{imageURL}</p>
    </div>
); 

